import Phaser from '../phaser.js';

import constant from '../utils/Constant.js';

export default class Bomb extends Phaser.GameObjects.Sprite {

    constructor(context, x, y) {
        super(context, (x + 8 + constant.ORIGIN_X) * constant.SCALE, (y + 8 + constant.ORIGIN_Y) * constant.SCALE);

        this.setDepth(1);
        this.setScale(constant.SCALE / 2.5, constant.SCALE / 2.5);

        this.anims.play('bomb_animation', true);
    }

    destroyBomb() {
        this.destroy();
    }
}
