import Phaser from '../phaser.js';

import constant from '../utils/Constant.js';

export default class Brick extends Phaser.GameObjects.Sprite {

    constructor(context, x, y) {
        super(context, (x + 8 + constant.ORIGIN_X) * constant.SCALE, (y + 8 + constant.ORIGIN_Y) * constant.SCALE, 'brick_1');

        this.setDepth(0);
        this.setScale(constant.SCALE / 2.5, constant.SCALE / 2.5);
    }

    destroyBrick() {
        this.anims.play('brick_animation', true);

        this.on('animationcomplete', function () {
            this.destroy();
        }.bind(this));
    }
}
