import Phaser from '../phaser.js';

class PowerupItem extends Phaser.GameObjects.Sprite {

    constructor(context, x, y, texture) {
        super(context, x, y, texture);

        this.setDepth(0);
        this.setScale(0);

        context.tweens.add({
            targets: this,
            scaleX: 1,
            scaleY: 1,
            duration: 250,
            ease: 'Back.easeOut'
        });
    }
}
