import Brick from '../object/Brick.js';

export default class BrickManager {

    constructor() {
        this.bricks = [];
        for (let i = 0; i < 25; i++) {
            let temp = [];
            for (let j = 0; j < 15; j++)
                temp.push(null);
            this.bricks[i] = temp;
        }
    }

    setBrick(context, bricks) {
        let newBombs = [];
        for (let i = 0; i < 25; i++) {
            let temp = [];
            for (let j = 0; j < 15; j++)
                temp.push(null);
            newBombs[i] = temp;
        }


        bricks.forEach(function (item) {
            newBombs[item.x / 16][item.y / 16] = true;
        });
        for (let i = 0; i < 25; i++)
            for (let j = 0; j < 15; j++) {
                if (this.bricks[i][j] == null && newBombs[i][j])
                    this.bricks[i][j] = context.add.existing(new Brick(context, 16 * i, 16 * j));
                if (this.bricks[i][j] != null && !newBombs[i][j]) {
                    this.bricks[i][j].destroyBomb();
                    this.bricks[i][j] = null;
                }
            }
    }
}
